mapper.update:
update z_user
set login_name = #{loginName,jdbcType=VARCHAR},
  login_pass = #{loginPass,jdbcType=CHAR},
  sex = #{sex,jdbcType=TINYINT},
  add_time = #{addTime,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, login_name, login_pass, sex, add_time
from z_user
where id = #{id,jdbcType=INTEGER}
